/** @jsxImportSource @emotion/react */ 
import logo from './logo.svg';
import './App.css';
import { useState,useEffect,useRef } from 'react';
import { css } from '@emotion/react'

const COLUMN_BG_COlORS = {
  loading:"#E3E3E3",
  todo:"#C9AF97",
  ongoing:"#FFE799",
  done:"#C0E8BA"
}
const DATA_STORE_KEY = "kanban-data-store"
const COLUMN_KEY_TODO = "todo"
const COLUMN_KEY_ONGOING = "ongoing"
const COLUMN_KEY_DONE = "done"


const KanbanNewCard = ({onSubmit}) => {
  const [title, setTitle] = useState('');
  const inputElem = useRef(null)
  const handleChange = (evt)=>{
    setTitle(evt.target.value)
  }
  useEffect(()=>{
    inputElem.current.focus()
    console.log(inputElem.current.value)
  },[])
  const handleKeyDown = (evt)=>{
    if(evt.key === "Enter"){
      onSubmit(title)
    }
  }
  return (
    <li css={kanbanCardStyles}>
      <h3>添加新卡片</h3>
      <div css={css`
          ${kanbanCardStylesTitle}
          & > input[type="text"]{
             width: 80%;
          }
        `}
      >
        <input 
          type="text" 
          value={title}
          ref={inputElem}
          onChange={handleChange} 
          onKeyDown={handleKeyDown}
        />
      </div>
    </li>
  );
};
const kanbanCardStyles = css`
  margin-bottom: 1rem;
  padding: 0.6rem 1rem;
  border: 1px solid gray;
  border-radius: 1rem;
  list-style: none;
  background-color: rgba(255, 255, 255, 0.4);
  text-align: left;
  &:hover{
    box-shadow: 0 0.2rem 0.2rem rgba(0,0,0,0.2),inset 0 1px #fff;
  }
`
const kanbanCardStylesTitle = css`
  min-height: 3rem;
`

const MINUTE = 60 * 1000;
const HOUR = 60 * MINUTE;
const DAY = 24 * HOUR;
const UPDATE_INTERVAL = MINUTE;
const KanbanCard = ({ title, status, onDragStart}) => {
  console.log(111)
  const [displayTime, setDisplayTime] = useState(status);
  useEffect(() => {
    const updateDisplayTime = () => {
      const timePassed = new Date() - new Date(status);
      let relativeTime = '刚刚';
      if (MINUTE <= timePassed && timePassed < HOUR) {
        relativeTime = `${Math.ceil(timePassed / MINUTE)} 分钟前`;
      } else if (HOUR <= timePassed && timePassed < DAY) {
        relativeTime = `${Math.ceil(timePassed / HOUR)} 小时前`;
      } else if (DAY <= timePassed) {
        relativeTime = `${Math.ceil(timePassed / DAY)} 天前`;
      }
      setDisplayTime(relativeTime);
    };
    const intervalId = setInterval(updateDisplayTime, UPDATE_INTERVAL);
    updateDisplayTime();

    return function cleanup() {
      clearInterval(intervalId);
    };
  }, [status]);

  const handleDragStart = (evt)=>{
    console.log(evt,'evt');
    evt.dataTransfer.effectAllowed = "move"
    evt.dataTransfer.setData("text/plain",title);
    onDragStart && onDragStart(evt)
  }
  return (
    <li css={kanbanCardStyles} draggable onDragStart={handleDragStart}>
      <div css={kanbanCardStylesTitle}>{title}</div>
      <div css={
        css`text-align: right;
        font-size: 0.8rem;
        color: #333;`} 
        title={status}
      >
        {displayTime}
      </div>
    </li>
  );
};

function App() {
  const [showAdd, setShowAdd] = useState(false); 
  const [todoList,setTodoList] = useState([
    { title: '开发任务-1', status: '2022-09-15 21:10' },
    { title: '开发任务-3', status: '2022-05-22 18:15' },
    { title: '开发任务-5', status: '2022-05-22 21:05' },
  ])
  const [ongoingList,setOngoingList] = useState([
    { title: '开发任务-4', status: '2022-05-02 18:15' },
    { title: '开发任务-6', status: '2022-05-22 18:15' },
    { title: '测试任务-2', status: '2022-05-12 18:15' }
  ])
  const [doneList,setDoneList] = useState([
    { title: '开发任务-9', status: '2022-11-02 21:10' },
    { title: '开发任务-7', status: '2022-11-22 18:15' },
    { title: '开发任务-5', status: '2022-11-22 21:05' },
  ])
  const [isLoading,setIsLoading] = useState(true)

  const [draggedItem,setDraggedItem] = useState(null)
  const [dragSource,setDragSource] = useState(null)
  const [dragTarget,setDragTarget] = useState(null)


  const handleAdd = (evt) => {  
    console.log(evt)
    setShowAdd(true) 
  };

  useEffect(()=>{
    const data = window.localStorage.getItem(DATA_STORE_KEY);
    setTimeout(()=>{
      if(data){
        const kanbanColumnData = JSON.parse(data); 
        setTodoList(kanbanColumnData.todoList);
        setOngoingList(kanbanColumnData.ongoingList);
        setDoneList(kanbanColumnData.doneList);
      }
      setIsLoading(false)
    },1000)
  },[])

  const KanBanBoard = ({ children })=>
    <main
      css={css`
        flex: 10;
        display: flex;
        flex-direction: row;
        gap: 1rem;
        margin: 0 1rem 1rem;
      `}
    >{children}</main>

  const KanBanColumn = ({ 
    children,
    bgColor,
    title,
    setIsDragSource=()=>{},
    setIsDragTarget=()=>{},
  })=>{
    return (
      <section 
        onDragStart={()=>setIsDragSource(true)}
        onDragOver={(evt) => { 
          evt.preventDefault();
          evt.dataTransfer.dropEffect = 'move';
          setIsDragTarget(true) 
        }} 
        onDragLeave={(evt) => { 
          evt.preventDefault(); 
          evt.dataTransfer.dropEffect = 'none'; 
          setIsDragSource(false)
        }} 
        onDrop={(evt) => { 
          evt.preventDefault();
        }} 
        onDragEnd={(evt) => { 
          evt.preventDefault(); 
          setIsDragSource(false)
          setIsDragTarget(false) 
        }}
        css={css`
          flex: 1 1;
          display: flex;
          flex-direction: column;
          border: 1px solid gray;
          border-radius: 1rem;
          background-color: ${bgColor};
          & > ul {
            flex: 0px;
            margin: 1rem;
            padding: 0;
            overflow: auto;
          }
          & > h2 {
            margin: 0.6rem 1rem;
            padding-bottom: 0.6rem;
            border-bottom: 1px solid gray;
            & > button { 
              float: right; 
              margin-top: 0.2rem;
              padding: 0.2rem 0.5rem; 
              border: 0; 
              border-radius: 1rem; 
              height: 1.8rem; 
              line-height: 1rem; 
              font-size: 1rem;} 
          }
        `}
      >
        <h2>{title}</h2>
        <ul>{children}</ul>
      </section>
    )
  }

  const handleSubmit = (title)=>{
    setTodoList( currentTodoList => [
      { title, status: new Date().toDateString()},
      ...currentTodoList
    ])
    todoList.unshift({ title, status: new Date().toDateString() });
  }

  const handleSaveAll = ()=>{
    const data = JSON.stringify({
      todoList,
      ongoingList,
      doneList
    })
    window.localStorage.setItem(DATA_STORE_KEY,data)
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>我的看板<button onClick={handleSaveAll}>保存所有卡片</button></h1>
        <img src={logo} className="App-logo" alt="logo" />
      </header>
      <KanBanBoard>
        { isLoading ?
          (<KanBanColumn bgColor={COLUMN_BG_COlORS["loading"]} title={"处理中"}/>)
          :(<KanBanColumn 
            bgColor = {COLUMN_BG_COlORS["todo"]}
            title={
            <>
              待处理
              <button onClick={handleAdd} disabled={showAdd}>
              ⊕ 添加新卡片
              </button>
            </>}
            setIsDragSource={(isSrc) => setDragSource(isSrc ? COLUMN_KEY_TODO : null)}
            setIsDragTarget={(isTgt) => setDragTarget(isTgt ? COLUMN_KEY_TODO : null)}
          >
            {showAdd && <KanbanNewCard onSubmit={handleSubmit}/>}
            {todoList.map(props => <KanbanCard onDragStart={()=>setDraggedItem(props)} {...props}  key={props.title}/>) }
            </KanBanColumn>
        )}
        <KanBanColumn 
          bgColor = {COLUMN_BG_COlORS["ongoing"]}  
          title="进行中"
          setIsDragSource={(isSrc) => setDragSource(isSrc ? COLUMN_KEY_ONGOING : null)}
          setIsDragTarget={(isTgt) => setDragTarget(isTgt ? COLUMN_KEY_ONGOING : null)}
        >
            { ongoingList.map(props => <KanbanCard onDragStart={()=>setDraggedItem(props)} {...props} key={props.title}/>) }
        </KanBanColumn>
        <KanBanColumn  
          bgColor = {COLUMN_BG_COlORS["done"]} title="已完成">
            { doneList.map(props => <KanbanCard onDragStart={()=>setDraggedItem(props)} {...props} key={props.title} />) }
        </KanBanColumn>
      </KanBanBoard>
    </div>
  );
}

export default App;
